#!/bin/bash
# Скрипт для автоматической генерации и настройки SSH ключа для GitHub Actions деплоя

set -e

GREEN='\033[0;32m'
YELLOW='\033[1;33m'
RED='\033[0;31m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

echo -e "${BLUE}🔐 Автоматическая настройка SSH для GitHub Actions деплоя${NC}"
echo ""

# Проверка аргументов
if [ -z "$1" ]; then
    echo -e "${RED}Использование: $0 <server-ip-or-domain> [server-user]${NC}"
    echo "Пример: $0 93.183.91.110 root"
    echo "Пример: $0 example.com root"
    exit 1
fi

SERVER_HOST=$1
SERVER_USER=${2:-root}
SSH_KEY_NAME="github_actions_deploy"
SSH_KEY_PATH="$HOME/.ssh/$SSH_KEY_NAME"

echo -e "${YELLOW}📋 Параметры:${NC}"
echo "   Сервер: $SERVER_USER@$SERVER_HOST"
echo "   SSH ключ: $SSH_KEY_PATH"
echo ""

# Проверка существования ключа
if [ -f "$SSH_KEY_PATH" ]; then
    echo -e "${YELLOW}⚠️  SSH ключ уже существует: $SSH_KEY_PATH${NC}"
    read -p "Перезаписать существующий ключ? (y/N): " -n 1 -r
    echo
    if [[ ! $REPLY =~ ^[Yy]$ ]]; then
        echo -e "${YELLOW}Использование существующего ключа...${NC}"
    else
        echo -e "${YELLOW}🗑️  Удаление старого ключа...${NC}"
        rm -f "$SSH_KEY_PATH" "$SSH_KEY_PATH.pub"
    fi
fi

# Генерация SSH ключа
if [ ! -f "$SSH_KEY_PATH" ]; then
    echo -e "${YELLOW}🔑 Генерация нового SSH ключа...${NC}"
    ssh-keygen -t ed25519 -C "github-actions-deploy" -f "$SSH_KEY_PATH" -N ""
    echo -e "${GREEN}✅ SSH ключ создан${NC}"
else
    echo -e "${GREEN}✅ Используется существующий SSH ключ${NC}"
fi

# Установка правильных прав
chmod 600 "$SSH_KEY_PATH"
chmod 644 "$SSH_KEY_PATH.pub"

# Копирование публичного ключа на сервер
echo ""
echo -e "${YELLOW}📤 Копирование публичного ключа на сервер...${NC}"
echo "   Это может потребовать ввода пароля сервера"

# Пробуем скопировать ключ
if ssh-copy-id -i "$SSH_KEY_PATH.pub" "$SERVER_USER@$SERVER_HOST" 2>/dev/null; then
    echo -e "${GREEN}✅ Публичный ключ успешно скопирован на сервер${NC}"
else
    echo -e "${YELLOW}⚠️  Автоматическое копирование не удалось. Выполните вручную:${NC}"
    echo ""
    echo "   ssh-copy-id -i $SSH_KEY_PATH.pub $SERVER_USER@$SERVER_HOST"
    echo ""
    read -p "Продолжить без автоматического копирования? (y/N): " -n 1 -r
    echo
    if [[ ! $REPLY =~ ^[Yy]$ ]]; then
        exit 1
    fi
fi

# Проверка подключения
echo ""
echo -e "${YELLOW}🔍 Проверка SSH подключения...${NC}"
if ssh -i "$SSH_KEY_PATH" -o ConnectTimeout=5 -o StrictHostKeyChecking=no "$SERVER_USER@$SERVER_HOST" "echo 'SSH connection successful'" 2>/dev/null; then
    echo -e "${GREEN}✅ SSH подключение работает!${NC}"
else
    echo -e "${RED}❌ SSH подключение не работает${NC}"
    echo ""
    echo "Возможные причины:"
    echo "  1. Публичный ключ не добавлен на сервер"
    echo "  2. Неправильный IP адрес или пользователь"
    echo "  3. SSH порт закрыт в firewall"
    echo ""
    echo "Попробуйте вручную:"
    echo "  ssh -i $SSH_KEY_PATH $SERVER_USER@$SERVER_HOST"
    exit 1
fi

# Вывод приватного ключа для GitHub Secrets
echo ""
echo -e "${BLUE}━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━